class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0, count = 1;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1' && s[i + 1] == '0') {
                i--;
                ans += count;
                while (i >= 0 && s[i] == '1') {
                    ans += count;
                    i--;
                }
                count++;
            }
        }
        return ans;
    }
};
