class Solution {
public:
    int maxOperations(string s) {
        int re = 0,n=s.size(),cnt = 0;
        for(int i=0;i<n;i++){
            if(s[i]-'0') {
                cnt++;
                if(i+1<n && s[i+1]=='0') {
                    re+=cnt;
                }
            }
        }
     return re;   
    }
};
