class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        c = ans = 0
        i = n-1
        while i >= 0:
            if s[i] == '1':
                ans += c
            else:
                c += 1
                while i-1 >= 0 and s[i-1] == '0':
                    i -= 1
            i -= 1
        return ans
