class Solution {
public:
    int maxOperations(string s) {
        vector<int> p;
        p.push_back(0);
        for(auto i:s){
            if(i=='1')
                p.push_back(p.back()+1);
            else
                p.push_back(p.back());
        }
        int res=0;
        for(int i=0;i<s.size()-1;i++){
            if(s[i]=='1' && s[i+1]=='0'){
                res+=(p[i+1]);
            }
        }
        return res;
    }
};
