class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int prev=-1;
        int cnt=0;
        int lastzero=-1;
        for(auto i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                if(prev!=-1 and prev+1!=i)
                {
                    ans+=cnt;
                }
                prev=i;
                cnt++;
            }
            else lastzero=i;
            // cout<<i<<' '<<prev<<' '<<cnt<<' '<<ans<<endl;
        }
        if(s.back()=='0')
        {
            ans+=cnt;
        }
        // cout<<cnt<<' ';
        return ans;
    }
};
