class Solution {
public:
    int maxOperations(string s) {
        int res = 0, c=0, i, n = s.size(), fl=0;
        for (i=n-1;i>=0;i--) {
            if (s[i]=='1') {
                if (fl) {
                    c++;
                }
                res += c;
                fl = 0;
            }
            else {
                fl = 1;
            }
        }
        return res;
    }
};
