class Solution {
public:
    int maxOperations(string s) {
        int N = s.size();
        vector<pair<char,int>> v;

        for(int i=0, j=1; i<N; i=j++) {
            while(j<N && s[j]==s[j-1]) { ++j; }
            v.emplace_back(s[i], j-i);
        }

        int onesFound = 0;        
        int M = v.size();
        int total = 0;
        for(int i=0; i<M; ++i) {
            if(v[i].first == '1') {
                onesFound += v[i].second;
            }
            else {
                total += onesFound;
            }
        }

        return total;
    }
};
