class Solution {
    public int maxOperations(String s) 
    {
        int n=s.length();
        int x=0;
        int ans=0;
        s+="1";
        n++;
        int last=-1;
        for(int i=0;i<n;i++)
        {
            if(i>0 && last !=i-1 && s.charAt(i)=='1' )
            {
                ans+=x;
                // x++;
                last=i;
            }
            if(s.charAt(i)=='1')
            {
                x++;last=i;
            }
        }

        return ans;
    }
}
