class Solution {
public:
    int maxOperations(string s) {
        int operations = 0;
        int oneCount = 0;
        bool wasLastOne = false;
        for (char c : s) {
            if (c == '1') {
                oneCount++;
                wasLastOne = true;
            } else {
                if (wasLastOne) {
                    operations += oneCount;
                }
                wasLastOne = false;
            }
        }
        return operations;
    }
};
