class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        long long ans=0;
        for(int i = (int)s.size()-1;i>=0;i--) {
            if(s[i] == '0' and (i == (int)s.size()-1 or s[i+1] == '1')) {
                cnt++;
            }
            else if(s[i] == '1') {
                ans+=cnt;
            }
        }
        return ans;

    }
};
