class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cnt = 0
        n = len(s)
        i = n-2
        if s[n-1] == '0':
            cnt += 1
        while i >= 0:
            if s[i] == '1':
                ans += cnt
            elif s[i] == '0' and s[i+1] == '1':
                cnt += 1
            i -= 1
        return ans
