class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ones = 0;
        int ans = 0;
        for(int i=0;i<n-1;i++){
            if(s[i] == '1' and s[i+1] == '0'){
                ones++;
                ans+=ones;
            }
            if(s[i] == '1' and s[i+1] != '0'){
                ones++;
            }
        }
        return ans;
    }
};
