class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        bool have = false;
        vector<int>bits;
        for(char c : s){
            if(c == '1'){
                bits.push_back(1);
                have = true;
            }
            else if ( c == '0' && have){
                bits.push_back(0);
                have = false;
            }
        }
        for(int bit : bits){
            if(bit == 1) cnt++;
            else ans+= cnt;
        }
        return ans;
    }
};
