class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> sum(n + 1);
        for (int i = 1; i <= n; i++) {
            sum[i] += sum[i - 1];
            if (s[i - 1] == '1')  sum[i]++;
        }
        int ans = 0;
        for (int i = 0; i < n - 1; i++)
            if (s[i] == '1' && s[i + 1] == '0')
                ans += sum[i + 1];
        return ans;
    }
};
