class Solution {
    public function maxOperations($s) {
        $ans = 0;
        $p = 0;
        $r = 0;
        $k = false;
        $h = [];

        for ($i = 0; $i < strlen($s); $i++) {
            if ($s[$i] == '1') {
                $h[] = 1;
                $k = true;
            }
            if ($s[$i] == '0' && $k == true) {
                $h[] = 0;
                $k = false;
            }
        }

        foreach ($h as $it) {
            if ($it == 1) {
                $p++;
            } else {
                $ans += $p;
            }
        }

        return $ans;
    }
}

