class Solution {
public:
    int maxOperations(string s) {
        reverse(begin(s), end(s));
        int n = s.length();
        int ans = 0;
        int cur = 0;
        for (int i = 0; i < n-1; i++) {
            if (s[i] == '0' && s[i+1] == '1') {
                cur++;
                ans += cur;
            } else if (s[i] == '1' && s[i+1] == '1') {
                ans += cur;
            }
        }
        return ans;
    }
};
