class Solution:
    def maxOperations(self, s: str) -> int:
        c1 = 0
        ans = 0
        f = False
        for i,ch in enumerate(s):
            if ch == '0':
                f = True
            else:
                if f:
                    ans += c1
                c1 += 1
                f = False
        if f:
            ans += c1

        return ans
