class Solution {
public:

    int maxOperations(string s) {
        int n=s.size();
        int ops=0;
        int i=0,ones=0;
        while(i<n&&s[i]=='0')i++;
        if(i==n)return 0;
        ones++;
        int prev=1;
        for(i=i+1;i<n;i++){
            if(s[i]=='0'&&prev)ops+=ones,prev=0;
            else if(s[i]=='1') ones++,prev=1;
        }
        return ops;
    }
};
