class Solution:
    def maxOperations(self, s: str) -> int:
        one_count = 0
        had_zero = False
        out = 0
        for i in range(len(s)):
            if s[i] == "1" and had_zero:
                out += one_count
                had_zero = False

            if s[i] == "1":
                one_count += 1
            else:
                had_zero = True

        if had_zero:
            out += one_count

        return out
