class Solution {
public:
    int maxOperations(std::string s) {
       int n = s.size(), tmp = 0, ans = 0;
       for(int i = 0; i < n; i++) {
          if(s[i] == '0') continue;
          if(s[i] == '1') tmp++;
          if(i + 1 < n && s[i + 1] == '0') {
            ans += tmp;
          }
       }
       return ans;
    }
};
