class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        res = 0
        zeroes = 0
        prev_zero = False
        for i in s[::-1]:
            if i=='0':
                if not prev_zero:
                    zeroes+=1
                    prev_zero = True
            else:
                res+=zeroes
                prev_zero = False
        return res
