class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.length(), precnt = 0;
        bool pre = false;
        for(int i=n-1; i>=0; i--) {
            if(s[i]=='0') pre = true;
            if(s[i]=='1') {
                if(pre) {
                    ans += precnt+1;
                    precnt++;
                    pre = false;
                } else {
                    ans += precnt;
                }
            }
        }
        return ans;
    }
};
