class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = s[0]=='1'?1:0, n = s.size();
        for (int i =1;i<n;i++)
        {
            if(s[i]=='0' && s[i-1]=='0')continue;
            else if (s[i]=='1')cnt++;
            else{
                ans += cnt;
            }
        }
        return ans;
    }
};
