class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int val=0;
        for(int i=0;i<n;i++){
            int count=0;
            while(i<n && s[i]=='1'){
                count++;
                i++;
            }

            if(count>0 && i<n){
                val+=count;
                ans+=val;
            }

        }
        return ans;
    }
};
