class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0, zero = 0;
        s += '1';
        int n = s.length();
        for(int i=0;i<n;i++) {
            if(s[i] == '1') {
                if(zero)
                ans += cnt;
                cnt += 1;
                zero = 0;
            }
            else zero = 1;
        }
        return ans;
    }
};
