class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int n=s.length();
        s+='1';
        int cnt=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'&&s[i+1]=='1')res+=cnt;
            else if(s[i]=='1')cnt++;
        }
        return res;
    }
};
