class Solution {
public:
    int maxOperations(string s) {
        int len = s.length(), res = 0, one = 0, zero = 0;
        if(s[len - 1] == '0') zero++;
        for(int i = len - 2; i >= 0; i--) {
            if(s[i] == '1'){
                one++;
                res += zero;
            }else if(s[i] == '0' && s[i + 1] != '0') zero++;
        }
        return res;
    }
};
