class Solution {
public:
    int maxOperations(string s) {
        vector <int> arr;
        int cnt = 0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                if(cnt>0)
                arr.push_back(cnt);
                cnt = 0;
            }
            else{
                cnt+=1;
            }
        }
        arr.push_back(cnt);
      //  for(auto &x:arr) cout<<x<<" ";
        int ans = 0;
        int curr = 0;
        for(int i=0;i<arr.size();i++){
            ans+=curr;
            curr+=arr[i];
        }
        return ans;
    }
};
