class Solution {
public:
    int maxOperations(string s) {
        int ans = 0 ;

        int one = 0;
        bool o = false;
        for(int i = 0 ; i < s.size() ; i++){
            if(s[i] == '1'){
                one++;
                o = true;
            }
            else if(s[i] == '0' && o){
                ans += one;
                o = false;
            }
        }
        return ans;
    }
};
