class Solution {
public:
    int maxOperations(string s) {
        int prev = 0;
        int ans = 0;
        while(s.size() && s.back() == '1') s.pop_back();
        for(int i = 0; i < s.size(); ) {
            if(s[i] == '1') {
                int cnt = 0;
                while(i < s.size() and s[i] == '1') {
                    i++;
                    cnt++;
                }
                prev += cnt;
                ans += prev;
            }
            else i++;
        }
        return ans;
    }
};
