class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int res = 0;
        int val = 0;
        for (int i = n - 1; i >= 0; --i)
        {
            if (s[i] == '1')
            {
                if ((i == n - 1 || s[i + 1] == '1'))
                {
                    res += val;
                }
                else
                {
                    ++val;
                    res += val;
                }
            }
        }
        return res;
    }
};
