class Solution {
public:
    int maxOperations(string s) {
        int maxop = 0;
        int op =0 ;
        int res=0;
        bool k = false;
        vector<bool>oper;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                oper.push_back(true);
                k = true;

            }
            if(s[i]=='0' && k==true){
                oper.push_back(false);
                k= false;
            }
        }
        for(auto it:oper){
            if(it==true){
                op++;

            }else{
                maxop+=op;
            }
        }
        return maxop;
    }
};
