class Solution:
    def maxOperations(self, s: str) -> int:
        vec = []
        ans = 0
        cur = 0

        for c in s:
            if c == '1':
                cur += 1
            else:
                if cur > 0:
                    vec.append(cur)

                cur = 0

        vec.append(cur)

        buff = 0
        for x in vec:
            buff += x
            ans += buff

        ans -= buff
        return ans
