class Solution {
public:
    int maxOperations(string s) {
        int ans=0,n=s.size(),c=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0') continue;
            else{
                if(i && s[i-1]=='0'){
                    ans+=c;
                }
                c++;
            }
        }
        if(s[n-1]=='0'){
            ans+=c;
        }
        return ans;
    }
};
