class Solution {
    public int maxOperations(String s) {
        int cntOne = 0, ans = 0;
        for (int i = 0; i < s.length() - 1; ++i) {
            if (s.charAt(i) == '1') {
                cntOne++;
                if (s.charAt(i + 1) == '0') {
                    ans += cntOne;
                }
            }
        }
        return ans;
    }
}
