class Solution {
public:
    int maxOperations(string s) {
        int ans = 0,cnt = 0;
        for(int i = 0;i < s.size();i++){
            if(s[i] == '1')++cnt;
            else{
                if(i && s[i] != s[i - 1])
                    ans += cnt;
            }
        }
        return ans;
    }
};
