class Solution:
    def maxOperations(self, s: str) -> int:
        prev = 0
        curr = 0

        res = 0
        for i in range(len(s)):
            if s[i] == '1':
                if i > 0 and s[i-1] == '0':
                    res += prev
                prev += 1
            if i == len(s)-1 and s[i] == '0':
                res += prev

        return res
