class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cur = 0
        n = len(s)
        for i in range(n):
            c = s[i]
            if c == '1':
                if i > 0 and s[i - 1] == '0':
                    ans += cur
                cur += 1
        if s[-1] == '0':
            ans += cur
        return ans
