class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        n = len(s)
        curr = 0
        prev = ''
        i = 0
        while i < n:
            if s[i] == '1' and prev == '0':
                res += curr
            if s[i] == '1':
                curr += 1
                prev = '1'
            else:
                prev = '0'
            i += 1
        if s[-1] == '0':
            res += curr
        return res
