class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n=s.size();

        string s2;
        for(int i=0;i<n;i++){
            if(s[i]=='1')s2.push_back(s[i]);
            else{
                s2.push_back(s[i]);
                for(int j=i;j<n;j++){
                    if(s[j]=='0'){
                        i=j;
                    }else break;
                }
            }
        }
        // cout<<s2<<"\n";
        n=s2.size();

        int curr=n-1;
        for(int i=n-1;i>=0;i--){
            if(s2[i]=='1'){
                ans+=curr-i;
                curr--;
            }
        }

        return ans;
    }
};
