mt19937 rng(random_device{}());
int rnd(int mod) { return rng() % mod; }

class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        s = " " + s;
        vector<int> sum(n + 1);
        for (int i = 1; i <= n; i++) {
            sum[i] = sum[i - 1] + (s[i] - '0');
        }

        int ans = 0;
        for (int i = n; i >= 1; i--) {
            if (s[i] == '1') continue;
            int j = i;
            while (j >= 1 && s[j] == '0') j--;
            ans += sum[j];
            i = j + 1;
        }
        return ans;
    }
};

