class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int res = 0;
        vector<int> num;
        int one = 0;
        int zero = 0;
        for(int i = 0; i < n; i++)
        {
            if(s[i] == '1') one++;
            else 
            {
                if(one) num.push_back(one);
                one = 0;
            }
        }
        int n1 = num.size();
        for(int i = 0; i < num.size(); i++)
        {
            res += ((n1 - i) * num[i]);
        }
        return res;
    }
};
