class Solution:
    def maxOperations(self, s: str) -> int:
        """
        1001101
        0011101
        0011011
        .. => 4

        010010
        000110
        """
        maxOps = 0
        prevOne = 0

        for i in range(len(s)):
            if s[i] == '1':
                prevOne += 1
            else:
                if i > 0 and s[i - 1] == '1':
                    maxOps += prevOne

        return maxOps
