class Solution {
public:
    int maxOperations(string s) {
        int ones=0,ans=0;
        int n = s.size();
        for(int i=0; i<n-1; i++)
        {
            if(s[i]=='1')ones++;
            if(s[i]=='1' && s[i+1]=='0')
            {
                ans+=ones;
            }
        }
        return ans;
    }
};
