class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int res = 0, one = 0;
        bool seenZero = false;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                if (seenZero) {
                    res += one;
                    seenZero = false;
                }
                one++;

            }
            if (s[i] == '0') seenZero = true;

        }

        if (seenZero) {
            res += one;
            seenZero = false;
        }
        return res;
    }
};
