class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int c = 0;
        int sz = s.length();
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                c++;
            } else {
                count += c;
                while (i < s.length() && s.charAt(i) == '0') {
                    i++;
                }
                i--;
            }
        }
        return count;
    }
}

