class Solution:
    def maxOperations(self, s: str) -> int:
        o = 0
        n = 0
        for i in range(len(s)):
            if s[i] == '1':
                o += 1
            elif i < len(s) - 1 and s[i] == '0' and s[i+1] == '1':
                n += o
            elif i == len(s) - 1 and s[i] == '0':
                n += o
        return n
