class Solution {
    public int maxOperations(String s) 
    {
        int n = s.length();
        int ans=0;

        int ones_count=s.charAt(0)=='1'?1:0;

        for(int i=1;i<n;i++)
        {
            char ch = s.charAt(i);

            if(ch=='0' && (i+1==n || s.charAt(i+1)=='1'))
            {
                ans+=ones_count;
            }
            else if(ch=='1')
            {
                ones_count++;
            }
        }
        return ans;
    }
}
