/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let rt = 0;
    let c = 0;
    let flag = false;
    for (let i = 0; i < s.length; i++) {
        if (s[i] === '1') {
            if (flag) {
                rt += c;
                flag = false;
            }
            c++;
        } else {
            flag = true;
        }
    }
    if (flag) rt += c;
    return rt;
};
