class Solution:
    def maxOperations(self, s: str) -> int:
        space = []
        n = len(s)
        for i in range(n-1):
            if s[i] == '1':
                if s[i+1] == '0':
                    space.append(True)
                else:
                    space.append(False)
        res= 0
        moves = 0
        for p in space[::-1]:
            if p:
                moves += 1
            res += moves
        return res
